content:
{%- assign product = all_products[section.settings.product] -%}
{%- assign current_variant = product.selected_or_first_available_variant -%}

{% if product == empty %}
  {%- assign section_onboarding = true -%}
  {%- assign onboarding_title = 'homepage.onboarding.product_title' | t -%}
{% endif %}

<div data-section-id="{{ section.id }}" data-section-type="product" itemscope itemtype="http://schema.org/Product">
  <meta itemprop="name" content="{{ product.title }}">
  <meta itemprop="url" content="{{ shop.url }}{{ product.url }}">
  <meta itemprop="image" content="{{ product.featured_image.src | img_url: '800x' }}">
  <meta itemprop="description" content="{{ product.description | strip_html | escape }}">
  {% comment %}
    {% if product.featured_image.src != blank %}
      <img src="{{ product.featured_image.src | img_url: '480x480' }}" alt="{{ featured_image.alt | escape }}" data-product-featured-image>
    {% else %}
      {{ 'product-1' | placeholder_svg_tag: 'placeholder-svg placeholder-svg--small' }}
    {% endif %}

    {% if product.images.size > 1 %}
      <ul>
        {% for image in product.images %}
          <li>
            <a href="{{ image.src | img_url: '480x480' }}" data-product-single-thumbnail>
              <img src="{{ image.src | img_url: '160x160' }}" alt="{{ image.alt | escape }}">
            </a>
          </li>
        {% endfor %}
      </ul>
    {% endif %}

    <h1>{{ product.title | default: onboarding_title }}</h1>
    {% if product.vendor != blank %}
      <p>{{ product.vendor }}</p>
    {% endif %}
  {% endcomment %}
  <div itemprop="offers" itemscope itemtype="http://schema.org/Offer">
    <meta itemprop="priceCurrency" content="{{ shop.currency }}">
    <meta itemprop="price" content="{{ product.price | divided_by: 100.00 }}">
    <link
      itemprop="availability"
      href="http://schema.org/{% if product.available %}InStock{% else %}OutOfStock{% endif %}"
    >

    <form action="/cart/add" method="post" enctype="multipart/form-data" class="button-product-f">
      {% comment %}
        {% unless product.variants.size == 1 and product.options.size == 1 and product.options.first == 'Title' and product.variants.first.title == 'Default Title' %}
          {% for option in product.options_with_values %}
            <div class="selector-wrapper js">
              <label
                {% if option.name == 'default' %}class="label-hidden"{% endif %}
                for="SingleOptionSelector-{{ section.id }}-{{ forloop.index0 }}">
                  {{ option.name }}
              </label>

              <select
                id="SingleOptionSelector-{{ section.id }}-{{ forloop.index0 }}"
                data-single-option-selector
                data-index="option{{ option.position }}">
                {% for value in option.values %}
                  <option
                    value="{{ value | escape }}"
                    {% if option.selected_value == value %}selected="selected"{% endif %}>
                      {{ value }}
                  </option>
                {% endfor %}
              </select>
            </div>
          {% endfor %}
        {% endunless %}
      {% endcomment %}

      <select name="id" class="no-js" data-product-select style="display:none">
        {% for variant in product.variants %}
          <option
            {% if variant == product.selected_or_first_available_variant %}
              selected="selected"
            {% endif %}
            {% unless variant.available %}
              disabled="disabled"
            {% endunless %}
            value="{{ variant.id }}"
          >
            {{ variant.title }}
          </option>
        {% endfor %}
      </select>
      {% comment %}
        <label for="Quantity-{{ section.id }}">{{ 'products.product.quantity' | t }}</label>
        <input type="number" id="Quantity-{{ section.id }}" name="quantity" value="1" min="1">
      {% endcomment %}

      <div data-price-wrapper style="display:none">
        <span data-product-price>
          {{ current_variant.price | default: '1999' | money }}
        </span>

        {% if product.compare_at_price_max > product.price %}
          <span class="visually-hidden" data-compare-text>{{ 'products.product.regular_price' | t }}</span>
          <s data-compare-price>
            {% if current_variant.compare_at_price > current_variant.price %}
              {{ current_variant.compare_at_price | money }}
            {% endif %}
          </s>
        {% endif %}
      </div>
      <input type="hidden" name="return_to" value="/checkout">
      <button
        class="btn-checkout"
        type="submit"
        name="add"
        data-add-to-cart
        {% unless product.available %}
          disabled="disabled"
        {% endunless %}
        {% if section_onboarding %}
          disabled="disabled"
        {% endif %}
      >
        <span data-add-to-cart-text>
          {% comment %}
            {% if product.available %}
              {{ 'products.product.add_to_cart' | t }}
            {% else %}
              {{ 'products.product.sold_out' | t }}
            {% endif %}
          {% endcomment %}
          BUY NOW
        </span>
      </button>

      {{- form | payment_button -}}
    </form>
  </div>

  {% unless product == empty %}
    <script type="application/json" data-product-json>
      {{ product | json }}
    </script>
  {% endunless %}
</div>
