{% if section.settings.header_style == 'header_style_1' %}
  {% if template == 'index' %}
    <header class="header-area header-padding-1 sticky-bar header-res-padding clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% else %}
    <header class="header-area header-in-container clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% endif %}

{% elsif section.settings.header_style == 'header_style_2' %}
  {% if template == 'index' %}
    <header class="header-area clearfix">
      <div class="header-top-area header-padding-2">
        {% include 'header-top' %}
      </div>
      <div class="header-bottom sticky-bar header-res-padding header-padding-2">
        {% include 'header-bottom' %}
      </div>
    </header>
  {% else %}
    <header class="header-area header-in-container clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% endif %}

{% elsif section.settings.header_style == 'header_style_3' %}
  {% if template == 'index' %}
    <div class="home-sidebar-left">
      {% include 'header-sidebar-area' %}
    </div>
    <header class="header-area header-padding-1 sticky-bar header-res-padding clearfix header-hm4-none">
      {% include 'header-bottom-2' %}
    </header>
  {% else %}
    <header class="header-area header-in-container clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% endif %}

{% elsif section.settings.header_style == 'header_style_4' %}
  {% if template == 'index' %}
    <header class="header-area sticky-bar header-padding-3 header-res-padding clearfix transparent-bar header-4">
      {% include 'header-bottom-3' %}
    </header>
  {% else %}
    <header class="header-area header-in-container clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% endif %}

{% elsif section.settings.header_style == 'header_style_5' %}
  {% if template == 'index' %}
    <header class="header-area sticky-bar header-padding-3 header-res-padding clearfix transparent-bar header-hm-7">
      {% include 'header-bottom-4' %}
    </header>
  {% else %}
    <header class="header-area header-in-container clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% endif %}

{% elsif section.settings.header_style == 'header_style_6' %}
  {% if template == 'index' %}
    <header class="header-area clearfix header-hm8">
      {% include 'header-top-2' %}
      <div class="header-bottom sticky-bar header-res-padding header-padding-2">
        {% include 'header-bottom-5' %}
      </div>
    </header>
  {% else %}
    <header class="header-area header-in-container clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% endif %}

{% elsif section.settings.header_style == 'header_style_7' %}
  {% if template == 'index' %}
    <header class="header-area clearfix header-hm9 transparent-bar">
      {% include 'header-top-3' %}
      <div class="header-bottom sticky-bar header-res-padding header-padding-2">
        {% include 'header-bottom-6' %}
      </div>
    </header>
  {% else %}
    <header class="header-area header-in-container clearfix">
      {% include 'header-top-4' %}
      <div class="header-bottom sticky-bar header-res-padding">
        {% include 'header-bottom-7' %}
      </div>
    </header>
  {% endif %}

{% else %}
  <header class="header-area header-in-container clearfix">
    {% include 'header-top-4' %}
    <div class="header-bottom sticky-bar header-res-padding">
      {% include 'header-bottom-7' %}
    </div>
  </header>
{% endif %}

{% include 'clickable-mainmenu' %}

<style>
  .theme-logo img{
    max-width: {{ section.settings.logo_width }}px;
  }
</style>

<script>

    {% if settings.stickey_header_enable %}

  /*--
  Menu Stick
  -----------------------------------*/
  var header = $('.sticky-bar');
  var win = $(window);
  win.on('scroll', function() {
      var scroll = win.scrollTop();
      if (scroll < 200) {
          header.removeClass('stick');
      } else {
          header.addClass('stick');
      }
  });

  {% endif %}
</script>
