{% if section.settings.product_tab_enable %}
  <div class="description-review-area" id="section-{{ section.id }}">
    <div class="{% if section.settings.container_style == 'style_1' %}container {% else %} container-fluid{% endif %}">
      <div class="description-review-wrapper">
        <div class="description-review-topbar nav">
          {% if section.settings.product_info_tab_enable %}
            <a
              href="#pro-dec"
              data-toggle="tab"
              role="tab"
              {% if section.settings.tab_active_handle == 'info_tab' %}
                class="active" aria-selected="true"
              {% endif %}
            >
              {{ section.settings.product_info_tab_title }}
            </a>
          {% endif %}
          {% if section.settings.product_review_tab_enable %}
            <a
              href="#pro-review"
              data-toggle="tab"
              role="tab"
              {% if section.settings.tab_active_handle == 'review_tab' %}
                class="active" aria-selected="true"
              {% endif %}
            >
              {{ section.settings.product_review_tab_title }}
            </a>
          {% endif %}
          {% if section.settings.product_comments_enable %}
            <a
              href="#comment-box"
              data-toggle="tab"
              aria-selected="false"
              {% if section.settings.tab_active_handle == 'comment_tab' %}
                class="active" aria-selected="true"
              {% endif %}
            >
              {{ section.settings.product_comments_title }}
            </a>
          {% endif %}
          {% assign i = 1 %}
          {% for block in section.blocks %}
            <a
              href="#{{ pages[block.settings.custom_tab_handle].title | handle }}-{{ block.id }}"
              data-toggle="tab"
              role="tab"
              aria-selected="false"
            >
              {{ block.settings.custom_tab_title }}
            </a>
            {% assign i = i | plus: 1 %}
          {% endfor %}
        </div>
        <div class="tab-content description-review-bottom">
          {% if section.settings.product_info_tab_enable %}
            <div
              class="tab-pane {% if section.settings.tab_active_handle == 'info_tab' %}active{% endif %}"
              id="pro-dec"
              role="tabpanel"
            >
              <div class="product-description-wrapper">
                {%- if product.description contains '[product_description]' -%}
                  {%- assign product_description_first = product.description
                    | split: '[/product_description]'
                    | first
                  -%}
                  {%- assign product_description_final = product_description_first
                    | split: '[product_description]'
                    | last
                  -%}
                  {{ product_description_final }}
                {%- else -%}
                  {{
                    product.description
                    | split: '[short_description]'
                    | first
                    | split: '[product_description]'
                    | first
                    | split: '[new_products]'
                    | first
                    | split: '[product_video]'
                    | first
                    | split: '[affiliate_link]'
                    | first
                    | split: '[countdown]'
                    | first
                  }}
                {%- endif -%}
              </div>
            </div>
          {% endif %}
          {% if section.settings.product_review_tab_enable %}
            <div
              class="tab-pane {% if section.settings.tab_active_handle == 'review_tab' %}active{% endif %}"
              id="pro-review"
              role="tabpanel"
            >
              <div class="product-anotherinfo-wrapper">
                <div id="shopify-product-reviews" data-id="{{product.id}}">{{ product.metafields.spr.reviews }}</div>
              </div>
            </div>
          {% endif %}
          {% if section.settings.product_comments_enable %}
            <div
              role="tabpanel"
              class="tab-pane {% if section.settings.tab_active_handle == 'comment_tab' %}active{% endif %}"
              id="comment-box"
            >
              <div class="product-anotherinfo-wrapper">
                <div class="fb-comment-box">
                  <!-- disqus comment box start -->
                  {% if section.settings.product_page_comments_box == 'disqus_comment' %}
                    {% include 'disqus-comment' %}
                  {% endif %}
                  <!-- disqus comment box end -->
                  <!-- facebook comment box start -->
                  {% if section.settings.product_page_comments_box == 'facebook_comment' %}
                    {% include 'facebook-comment' %}
                    {% include 'facebook-comment-box-script' %}
                  {% endif %}
                  <!-- facebook comment box end -->
                </div>
              </div>
            </div>
          {% endif %}
          {% assign i = 1 %}
          {% for block in section.blocks %}
            <div
              class="tab-pane"
              id="{{ pages[block.settings.custom_tab_handle].title | handle }}-{{ block.id }}"
              role="tabpanel"
            >
              <div class="product-anotherinfo-wrapper">
                {{ pages[block.settings.custom_tab_handle].content }}
              </div>
            </div>
            {% assign i = i | plus: 1 %}
          {% endfor %}
        </div>
      </div>
    </div>
  </div>
{% endif %}

{%- include 'section-style' -%}
